import InputSearch from './components/InputSearch.js'
import MainFooter from './components/MainFooter.js'
import HomeContent from './pages/HomeContent.js'
import SearchContent from './pages/SearchContent.js'
import Details from './pages/Details.js'
import { BrowserRouter as Router, Route } from 'react-router-dom'
import { useState } from 'react'
import axios from 'axios'

const App = () => {
  const [query, setQuery] = useState('')
  const [page, setPage] = useState(0)
  const [bookList, setBookList] = useState([])
  let timeout = {}

  const getBooks = async () => {
    if (!query) return
    const { data } = await axios.get(`https://www.googleapis.com/books/v1/volumes?maxResults=10&startIndex=${page}&q=${query}`)
    setPage(page + 1)
    const list = data.items.map((book) => {
      return {
        id: book.id,
        title: book.volumeInfo.title,
        author: book.volumeInfo.authors ? book.volumeInfo.authors[0] : 'Unkown author',
        description: book.volumeInfo.description || book.searchInfo?.textSnippet || '',
        imgCover: book.volumeInfo.imageLinks?.smallThumbnail || book.volumeInfo.imageLinks?.thumbnail
      }
    })
    setBookList([...bookList, ...list])
  }

  const updateQuery = (query) => {
    setQuery(query)
    if (query) {
      // change route here
      clearTimeout(timeout)
      timeout = setTimeout(() => {
        getBooks()
      }, 1000)
    }
  }

  return (
    <Router>
      <div className="app">
        <Route>
          <div className="main-page">
            <div className="content header">
              <InputSearch query={ query } onUpdate={ updateQuery } />
            </div>
            <div>
              <Route path="/home" exact component={ HomeContent } />
              <Route path="/search" exact>
                <SearchContent bookList={ bookList } />
              </Route>
            </div>
            <div>
              <MainFooter />
            </div>
          </div>
        </Route>
        <Route path="/details" exact>
          <Details />
        </Route>
      </div>
    </Router>
  );
}

export default App;
